import { Component, OnInit } from '@angular/core';
import { NotificationService } from 'src/app/shared/notification.service';

@Component({
  selector: 'app-addnotification',
  templateUrl: './addnotification.component.html',
  styleUrls: ['./addnotification.component.css']
})
export class AddnotificationComponent implements OnInit {

  notification={
    notificationmess:''
  };

  notifications:any;

  constructor(public serv:NotificationService){}

  ngOnInit():void{
    this.fetchmessage();
  }

  submit() {
    this.serv.addmessage(this.notification).subscribe(res => {
      alert('Message posted on homepage');
      this.notification = {
        notificationmess: ''
      };
      this.fetchmessage(); // Fetch messages again after posting a new message
    });
  }

  delmess(id: any) {
    this.serv.delmessage(id).subscribe(data => console.log(data));
    alert('Message deleted');
    this.fetchmessage(); // Fetch messages again after deleting a message
  }

  fetchmessage(): void {
    this.serv.viewmessage().subscribe(data => {
      this.notifications = data;
      console.log(this.notifications);
    });
  }

}

<form #addmess="ngForm" (ngSubmit)="submit()">
    <div class="form-group">
      <label for="notification" class="text-primary">Enter Notification Message</label>
      <input type="text" class="form-control" id="notification" placeholder="Message..." name="notificationmess" [(ngModel)]="notification.notificationmess" #notificationmess="ngModel">
    </div>
    <br>
      <button type="submit" class="button-86"><i class="bi bi-cloud-upload"></i> Post</button>
  </form>
