import { Component, Output, EventEmitter } from '@angular/core';
import { LoginService } from 'src/app/shared/link.service.spec';
import { LoginFormVisibilityService } from '../services/login-form-visibility.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  email: string = '';
  password: string = '';

  constructor(
    private loginService: LoginService,
    private loginFormVisibilityService: LoginFormVisibilityService
  ) {}

  @Output() submitted = new EventEmitter<void>();
  @Output() loginFormOpened = new EventEmitter<boolean>();

  toggleLoginForm() {
    // Get the current visibility of the login form from the service
    const currentVisibility = this.loginFormVisibilityService.showLoginForm$.getValue();
    // Toggle the visibility
    this.loginFormVisibilityService.setShowLoginFormVisibility(!currentVisibility);
    // Emit the event to update the visibility in the parent component (NavbarComponent)
    this.loginFormOpened.emit(!currentVisibility);
  }

  onSubmit() {
    // Implement your login logic here
    // For simplicity, we will just consider a successful login
    // You can add proper validation and authentication logic here

    // Emit the 'submitted' event when the form is submitted
    this.submitted.emit();

    // Call the login service to perform the API call to your backend server
    this.loginService.login(this.email, this.password).subscribe(
      (response) => {
        // Login successful
        alert('Login successful');
        // You can perform further actions here, such as redirecting the user to a dashboard page.
      },
      (error) => {
        // Login failed
        alert('Invalid credentials');
      }
    );

    // Hide the login form after successful login
    this.loginFormVisibilityService.setShowLoginFormVisibility(false);
  }
}
