// app.component.ts
import { Component, ElementRef, ViewChild } from '@angular/core';
import { LoginFormVisibilityService } from './shared/login-form-visiblity.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  showLoginForm: boolean = false;

  @ViewChild('appContainer') appContainerRef!: ElementRef;

  constructor(private loginFormVisibilityService: LoginFormVisibilityService) {
    this.loginFormVisibilityService.showLoginForm$.subscribe((visible: boolean) => {
      this.showLoginForm = visible;
      this.toggleBlurEffect();
    });
  }

  private toggleBlurEffect() {
    if (this.appContainerRef) {
      const appContainer = this.appContainerRef.nativeElement;
      if (this.showLoginForm) {
        appContainer.classList.add('blur-effect');
      } else {
        appContainer.classList.remove('blur-effect');
      }
    }
  }
}
