@Injectable({
  providedIn:'root'
})

export class authGuard{
  isToken!:boolean;
  
  constructor(private auth:LoginService,private route:Router){}
  
  canActivate(){
    
    if(this.auth.loggedIn()){
      return true;
    }
    else{
      this.route.navigate([''])
      return false
    }
  }

}
