import { Component } from '@angular/core';
import { LoginFormVisibilityService } from '..../';


@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  loggedIn: boolean = false;
  showLoginForm: boolean = false;
  showLogoutButton: boolean = false;

  
  login() {
    // Implement your login logic here
    this.showLoginForm = true;
  }

  logout() {
    // Implement your logout logic here
    this.loggedIn = false;
  }

  onLoginSubmitted() {
    // This function will be called when the login form is submitted
    // Implement your login logic here
    // For simplicity, we will just consider a successful login
    this.loggedIn = true;
    this.showLoginForm = false;
    this.showLogoutButton = true;
  }
}
<nav id="navbar" class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container">
    <a class="navbar-brand" href="#" ><img id="logo" src="assets/LOGO_ICTAK-Name.jpg" alt=""></a>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav ml-auto">
        <li class="nav-item" *ngIf="!loggedIn">
          <button class="btn btn-primary" (click)="login()">Login</button>
        </li>
        <li class="nav-item" *ngIf="loggedIn">
          <button class="btn btn-danger" (click)="logout()">Logout</button>
        </li>
      </ul>
    </div>
  </div>
</nav>

<app-login *ngIf="showLoginForm" (submitted)="onLoginSubmitted()"></app-login>
