import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string ='';
  password: string = '';
  

  onSubmit() {
    // Implement your login logic here
    // For simplicity, we will just consider a successful login
    // You can add proper validation and authentication logic here
    this.showLogoutButton();
  }

  private showLogoutButton() {
    // Show the logout button in the navbar
    const navbarComponent: any = document.querySelector('app-navbar');
    if (navbarComponent) {
      navbarComponent.loggedIn = true;
    }
  }
}

<!-- login.component.html -->
<div class="container mt-4">
  <div class="heading">
    <h3>User Log-In</h3>
  </div>
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="email">Email</label>
      <input type="email" class="form-control" id="email" name="email" required [(ngModel)]="email">
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input type="password" class="form-control" id="password" name="password" required [(ngModel)]="password">
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
