import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HomeComponent } from 'src/app/components/sidenav/home.component';
import { UserdataService } from 'src/app/shared/userdata.service';

@Component({
  selector: 'app-edituser',
  templateUrl: './edituser.component.html',
  styleUrls: ['./edituser.component.css']
})
export class EditUserComponent implements OnInit {
  userId!: string;
  user: any = {};

  constructor(private route: ActivatedRoute, private userDataService: UserdataService,private router:Router) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.userId = params['id'];
      this.userDataService.getUserById(this.userId).subscribe(
        (user) => {
          this.user = user;
        },
        (error) => {
          console.error(error);
          // Handle error if necessary
        }
      );
    });
  }

  updateUser(): void {
    if (this.user.newPassword) {
      // Handle password update
      this.user.password = this.user.newPassword;
    } else {
      // Remove password property if not updating password
      delete this.user.password;
    }
  
    console.log('Updating user:', this.user);
  
    this.userDataService.updateUser(this.userId, this.user).subscribe(
      (response) => {
        console.log('Update successful:', response.message);
        alert('Edited User')
        this.router.navigate(['home'])
        // Handle success or navigate to another page
      },
      (error) => {
        console.error('Update error:', error);
        // Handle error if necessary
      }
    );
  }
  
  
  
}
<div class="registration-form">
    <form class="bg-light" #edituser="ngForm" (ngSubmit)="updateUser()">
      <div class="form-icon">
        <span><i class="bi bi-person-exclamation"></i></span>
      </div>
      <div class="text-center headingdiv bg-primary-subtle fs-3 font-monospace">
        <span>Edit Officer Details</span>
      </div>
      <br>
      <div class="form-group">
        <input type="text" class="form-control item" id="username" placeholder="Name" [(ngModel)]="user.name" name="name">
      </div>
      <div class="form-group">
        <select class="form-select item" id="role" [(ngModel)]="user.role" name="role">
          <option value="" disabled selected>Role</option>
          <option>Training Head</option>
          <option>Placement Officer</option>
        </select>
      </div>
      <div class="form-group">
        <input type="text" class="form-control item" id="email" placeholder="Email" [(ngModel)]="user.email" name="email">
      </div>
      <div class="form-group">
        <input type="text" class="form-control item" id="phone-number" placeholder="Enter New Password" required [(ngModel)]="user.newPassword" name="newPassword"  #newPassword="ngModel" [class.is-invalid]="newPassword.invalid&&newPassword.touched" required pattern="^[a-zA-Z0-9]{6,10}$">
        <span *ngIf="newPassword.untouched &&newPassword.errors?.['required']" class="error-message" >Password is required.</span>
        <span *ngIf="newPassword.touched &&newPassword.errors?.['pattern']" class="error-message">Min 6 and Max 10 char. No Special Characters.</span>
        <span *ngIf="newPassword.touched &&!newPassword.errors?.['pattern']&&!newPassword.errors?.['required']" class="success-message">Valid</span>
      </div>
      <div class="form-group d-flex justify-content-evenly">
        <button type="submit" class="btn btn-block update-account" [disabled]="edituser.invalid">
            <i class="bi bi-person-fill-up"></i> Update
          </button>
        <button type="button" routerLink="/home" class="btn btn-block cancel-account"><i class="bi bi-x-circle-fill"></i> Cancel</button>
      </div>
    </form>
  </div>
  

<!-- scriptfiles -->
<script type="text/javascript" src="https://code.jquery.com/jquery-3.2.1.min.js"></script>
<script type="text/javascript" src="https://cdnjs.cloudflare.com/ajax/libs/jquery.mask/1.14.15/jquery.mask.min.js"></script>
<script src="assets/js/script.js"></script>
