import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Frontend';
  isLoginFormVisible: boolean = false;

  constructor(private loginFormVisibilityService: LoginFormVisibilityService) {
    this.loginFormVisibilityService.showLoginForm$.subscribe((visible: boolean) => {
      this.isLoginFormVisible = visible;
      if (this.isLoginFormVisible) {
        document.body.classList.add('blur-background');
      } else {
        document.body.classList.remove('blur-background');
      }
    });
  }
}
