import { Component,OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { StudentdataService } from 'src/app/shared/studentdata.service';

@Component({
  selector: 'app-viewstudents',
  templateUrl: './viewstudents.component.html',
  styleUrls: ['./viewstudents.component.css']
})
export class ViewstudentsComponent implements OnInit{
  studdetail:any;
  constructor(public serv:StudentdataService, public router:Router){}
  ngOnInit(): void {
    this.serv.getstudentdata().subscribe((data=>{
      this.studdetail=data;
      console.log(this.studdetail)
    }))
  }
  edititem(id:any){
    this.router.navigate(['editstuds/'+id]);
  }
  delitem(id:any){
    this.serv.delitem(id).subscribe(data=>console.log(data))
    alert('Data deleted')
    this.router.navigate(['viewstuds'])
  }
}
<app-navigation></app-navigation>

<!-- add button -->
<div class="addbtn m-3 text-center">
    <button class="btn btn-outline-success" routerLink="/addstuds"><i class="bi bi-person-plus"></i> Add new</button>
</div>

<table class="table table-dark table-hover table-striped ">
    <thead>
      <tr>
        <th scope="col">ID</th>
        <th scope="col">Name</th>
        <th scope="col">Course</th>
        <th scope="col">Project</th>
        <th scope="col">Batch</th>
        <th scope="col">Status</th>
        <th scope="col">Placement</th>
        <th scope="col">Edit</th>
        <th scope="col">Delete</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of studdetail">
        <td scope="row">{{item.id}}</td>
        <td>{{item.name}}</td>
        <td>{{item.course}}</td>
        <td>{{item.project}}</td>
        <td>{{item.batch}}</td>
        <td>{{item.status}}</td>
        <td>{{item.placement}}</td>
        <td scope="col"><a (click)="edititem(item._id)"><i class="bi bi-pencil-square text-primary"></i></a></td>
        <td scope="col"><a href="viewstuds" (click)="delitem(item._id)"><i class="bi bi-trash3-fill text-danger"></i></a></td>
      </tr>
    </tbody>
  </table>
