// navbar.component.ts
import { Component } from '@angular/core';
import { LoginFormVisibilityService } from 'src/app/shared/login-form-visiblity.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  loggedIn: boolean = false;
  showLoginForm: boolean = false;

  constructor(private loginFormVisibilityService: LoginFormVisibilityService) {
    this.loginFormVisibilityService.showLoginForm$.subscribe(
      (visibility: boolean) => {
        this.showLoginForm = visibility;
      }
    );
    this.loginFormVisibilityService.loggedIn$.subscribe(
      (loggedIn: boolean) => {
        this.loggedIn = loggedIn;
      }
    );
  }

  login() {
    // Implement your login logic here
    this.loginFormVisibilityService.setShowLoginFormVisibility(true);
    this.showLoginForm = true;
  }

  onLoginSubmitted() {
    // This function will be called when the login form is submitted
    // Implement your login logic here
    // For simplicity, we will just consider a successful login
    this.loggedIn = true;
    this.loginFormVisibilityService.setLoggedIn(true);
  }

  onCancel() {
    // Close the login form by hiding it
    this.loginFormVisibilityService.setShowLoginFormVisibility(false);
  }

  onLoginFormOpened(showLoginForm: boolean) {
    if (!showLoginForm) {
      // Show the login button again only if the login form is closed
      this.loginFormVisibilityService.setLoggedIn(false);
    }
  }
}
<header class="header">
  <div class="brand-box">
    <span class="brand">ICTAK</span>
    <a class="navbar-brand" href="#"><img id="logo" src="assets/LOGO_ICTAK-Name.jpg" alt=""></a>
  </div>
  
  <div class="text-box">
    <h1 class="heading-primary">
      <span class="heading-primary-main">Learner Tracker By</span>
      <span class="heading-primary-sub">ICT Academy of Kerala</span>
    </h1>
    <li class="nav-item" *ngIf="!loggedIn">
      <button class="btn btn-primary" (click)="login()">Login</button>
    </li>
  </div>
</header>

<app-login *ngIf="showLoginForm" (submitted)="onLoginSubmitted()" (loginFormOpened)="showLoginForm = $event"></app-login>
