import { Component } from '@angular/core';

@Component({
  selector: 'app-viewuser',
  

})
export class ViewuserComponent {

}




import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UserdataService } from 'src/app/userdata.service';

@Component({
  selector: 'app-edituser',
  templateUrl: './viewuser.component.html',
  styleUrls: ['./viewuser.component.css']
})
export class EdituserComponent implements OnInit {
  userId: string;
  user: any = {};

  constructor(private route: ActivatedRoute, private userDataService: UserdataService) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.userId = params['_id'];
      this.userDataService.getUserById(this.userId).subscribe(
        (user) => {
          this.user = user;
        },
        (error) => {
          console.error(error);
          // Handle error if necessary
        }
      );
    });
  }

  updateUser(): void {
    this.userDataService.updateUser(this.userId, this.user).subscribe(
      (response) => {
        console.log(response.message);
        // Handle success or navigate to another page
      },
      (error) => {
        console.error(error);
        // Handle error if necessary
      }
    );
  }
}
<app-navigation></app-navigation>

<div class="addbtn m-3 text-center">
  <button class="btn btn-outline-success" routerLink="/adduser"><i class="bi bi-person-plus"></i> Add new</button>
</div>

<!-- user details in card form -->
<div class="container text-center d-flex mt-5">
  <div class="row">
    <div class="col-6" *ngFor="let user of users">
      <div class="card border border-success p-2 mb-2 border-opacity-75" style="width: 18rem;">
        <div class="icon">
          <img src="assets/person_circle_icon_159926.png" class="card-img-top" alt="...">
        </div>
        <div class="card-body bg-body-tertiary">
          <h5 class="card-title">{{ user.role }}</h5>
          <p class="card-text">Name: {{ user.name }}</p>
          <p class="card-text">Email: {{ user.email }}</p>
          <div class="button d-flex justify-content-evenly">
            <a [routerLink]="'/edituser/' + user._id" class="btn btn-outline-primary"><i class="bi bi-pencil-square"></i> Edit</a>
            <button class="btn btn-outline-danger" ><i class="bi bi-trash3-fill"></i> Delete</button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
