import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class LoginFormVisibilityService {
  private showLoginFormSubject: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);
  public showLoginForm$: Observable<boolean> = this.showLoginFormSubject.asObservable();

  constructor() {}

  setShowLoginFormVisibility(visible: boolean): void {
    this.showLoginFormSubject.next(visible);
  }
}
