import { Component, OnInit , ElementRef, Renderer2, ViewChild } from '@angular/core';
import { Router } from '@angular/router';

import { INavbarData } from './helper';
import { navbarData } from './nav-data';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{

  

  isSidebarActive = false; // Add this property to control sidebar visibility
  @ViewChild('sidebar')
  sidebar!: ElementRef;
  dropdownStates: { [key: string]: boolean } = {}

  collapsed = false;
  navData: INavbarData[] = navbarData;
  isAddUserActive: boolean = false;
  isViewUserActive :boolean = false;

  constructor(private router:Router, private renderer: Renderer2){}

  ngOnInit(): void {
    
  }

 

  toggleCollapse(): void {
    this.collapsed = !this.collapsed;
  }

  logout(){
    // after implementing tokenization
    // localStorage.removeItem('token');
    this.router.navigate(['']);
  }

  toggleSidebar(): void {
    this.isSidebarActive = !this.isSidebarActive; // Toggle the sidebar visibility
    if (this.isSidebarActive) {
      this.renderer.addClass(this.sidebar.nativeElement, 'active');
    } else {
      this.renderer.removeClass(this.sidebar.nativeElement, 'active');
    }
    if (this.isSidebarActive) {
      this.isAddUserActive = false; // Hide AddUser when sidebar is toggled
    }
    
  }
  loadAddUser(): void {
    this.isAddUserActive = true;
    this.isSidebarActive = true; // Close sidebar when AddUser is loaded
    this.isViewUserActive=false;
  }
  
  loadViewUser(): void {
    this.isViewUserActive=true;
    this.isSidebarActive=true;
    this.isAddUserActive=false;
  }
  toggleDropdown(submenuId: string): void {
    const submenu = document.getElementById(submenuId);
    if (submenu) {
      submenu.classList.toggle('show');
    }
  }

}
<!-- Notification -->
<div class="card text-center bg-primary-subtle">
    <marquee behavior="scroll" direction="left" onMouseOver="this.stop()" onMouseOut="this.start()"><a href="">Notifications can be passed here !</a></marquee>
</div>

<!-- Side bar -->
<div class="wrapper">
    <nav #sidebar id="sidebar" [ngClass]="{'active': isSidebarActive}">
        <div class="sidebar-header">
            <h3>ICTAK Learner Tracker</h3>
        </div>
        <ul class="list-unstyled components">
            <!-- add home component *** -->
            <a routerLink="/home"><p><i class="bi bi-house"></i> Home</p></a> 
            <li>
                <a href="#homeSubmenu" data-toggle="collapse" aria-expanded="false" class="dropdown-toggle"><i class="bi bi-person-lock"></i> Admin Panel</a>
                <ul class="collapse list-unstyled" id="homeSubmenu">
                    <li>
                        <a (click)="loadAddUser()"><i class="bi bi-person-plus"></i> Add User</a>
                    </li>
                    <li>
                        <a (click)="loadViewUser()"><i class="bi bi-person-badge"></i> View Users</a>
                    </li>
                </ul>
            </li>
            <li>
                <a href="#pageSubmenu" data-toggle="collapse" aria-expanded="false" class="dropdown-toggle"><i class="bi bi-people-fill"></i> Student Panel</a>
                <ul class="collapse list-unstyled" id="pageSubmenu">
                    <li>
                        <a href="#" routerLink="/addstuds"><i class="bi bi-person-plus"></i> Add Students</a>
                    </li>
                    <li>
                        <a href="#" routerLink="/viewstuds"><i class="bi bi-person-badge"></i> View Students</a>
                    </li>
                    <li>
                        <a href="#" routerLink="/csv"><i class="bi bi-file-earmark-arrow-up-fill"></i> CSV-upload</a>
                    </li>
                </ul>
            </li>
            <li>
                <a class="nav-link" routerLink="/passchange"><i class="bi bi-gear"></i> Change Password</a>
            </li>
        </ul>
        <ul class="list-unstyled CTAs">
            <li>
                <a class="nav-link" routerLink=""><i class="bi bi-box-arrow-left"></i> LogOut</a>
            </li>
        </ul>
    </nav>

    <!-- Page Content  -->
    <div id="content">




        <nav class="navbar navbar-expand-lg navbar-light bg-light">
            <div class="container-fluid">

                <button (click)="toggleSidebar()" type="button" id="sidebarCollapse" class="btn btn-info">
                    <i class="fas fa-align-left"></i>
                    <span> Menu</span>
                </button>
                

                

            </div>
        </nav>



        <ng-container *ngIf="isAddUserActive">
            <app-adduser></app-adduser>
          </ng-container>
          <ng-container *ngIf="isViewUserActive">
            <app-view-user></app-view-user>
          </ng-container>


          <div>
