import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UserdataService } from 'src/app/userdata.service';

@Component({
  selector: 'app-edituser',
  templateUrl: './edituser.component.html',
  styleUrls: ['./edituser.component.css']
})
export class EditUserComponent implements OnInit {
  userId!: string;
  user: any = {};

  constructor(private route: ActivatedRoute, private userDataService: UserdataService) { }

  ngOnInit(): void {
    this.route.params.subscribe((params) => {
      this.userId = params['_id'];
      this.userDataService.getUserById(this.userId).subscribe(
        (user) => {
          this.user = user;
        },
        (error) => {
          console.error(error);
          // Handle error if necessary
        }
      );
    });
  }

  updateUser(): void {
    this.userDataService.updateUser(this.userId, this.user).subscribe(
      (response) => {
        console.log(response.message);
        // Handle success or navigate to another page
      },
      (error) => {
        console.error(error);
        // Handle error if necessary
      }
    );
  }
}
<app-navigation></app-navigation>

<div class="registration-form">
    <form class="bg-light">
        <div class="form-icon">
            <span><i class="bi bi-person-exclamation"></i></span>
        </div>
        <div class="text-center headingdiv bg-primary-subtle fs-3 font-monospace">
            <span>Edit Officer Details</span>
        </div>
        <br>
        <div class="form-group">
            <input type="text" class="form-control item" id="username" placeholder="Name">
        </div>
        <div class="form-group">
            <select id="disabledSelect" class="form-select item">
                <option>Training Head</option>
                <option>Placement Officer</option>
            </select>
        </div>
        <div class="form-group">
            <input type="text" class="form-control item" id="email" placeholder="Email">
        </div>
        <div class="form-group">
            <input type="text" class="form-control item" id="phone-number" placeholder="Password">
        </div>
        <div class="form-group d-flex justify-content-evenly">
            <button type="submit" class="btn btn-block update-account"><i class="bi bi-person-fill-up"></i> Update</button>
            <button type="button" routerLink="/viewuser" class="btn btn-block cancel-account"><i class="bi bi-x-circle-fill"></i> Cancel</button>
        </div>
    </form>
</div>

