import { Component, OnInit } from '@angular/core';
import { NotificationService } from 'src/app/shared/notification.service';

@Component({
  selector: 'app-viewmessage',
  templateUrl: './viewmessage.component.html',
  styleUrls: ['./viewmessage.component.css']
})
export class ViewmessageComponent implements OnInit{
  notification:any
}
<ul class="list-group" *ngFor="let item of notification">
    <li class="list-group-item">{{item.notification}} <a class="text-danger" (click)="delmess(item._id)"><i class="bi bi-calendar2-x"></i></a> </li>
</ul>
