import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { UserDataService } from '/u';
import { User } from '../models/user.model';

@Component({
  selector: 'app-adduser',
  templateUrl: './adduser.component.html',
  styleUrls: ['./adduser.component.css']
})
export class AdduserComponent {
  userForm: FormGroup;

  constructor(private fb: FormBuilder, private userDataService: UserDataService) {
    this.userForm = this.fb.group({
      name: [''],
      email: [''],
      password: [''],
      role: ['']
    });
  }

  onSubmit() {
    const user: User = this.userForm.value;
    this.userDataService.addUser(user).subscribe(
      (response) => {
        console.log('User added successfully:', response);
        // Add any additional logic or display success message here
      },
      (error) => {
        console.error('Error adding user:', error);
        // Display error message or handle the error here
      }
    );
  }
}
