import { Component, Output ,EventEmitter, importProvidersFrom } from '@angular/core';
import { LoginService } from 'src/app/shared/link.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],

})
export class LoginComponent {
  email : string='';
  password: string = '';
  showLoginForm: boolean = false;


  constructor(private loginService : LoginService) {}
  
  @Output() submitted = new EventEmitter<void>();
  @Output() loginFormOpened = new EventEmitter<boolean>();


   onSubmit() {
    this.submitted.emit();
    this.loginService.login(this.email, this.password)
      .subscribe(
        (response) => {
          // Login successful
          alert('Login successful');
          // You can perform further actions here, such as redirecting the user to a dashboard page.
        },
        (error) => {
          // Login failed
          alert('Invalid credentials');
        });
   
  }

  private showLogoutButton() {
    // Show the logout button in the navbar
    const navbarComponent: any = document.querySelector('app-navbar');
    if (navbarComponent) {
      navbarComponent.loggedIn = true;
    }
  }
}

<!-- login.component.html -->
<div class="container mt-4">
  <div class="heading">
    <h3>User Log-In</h3>
  </div>
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label class="label" for="email">Email</label>
      <input type="email" class="form-control" placeholder="Enter email" id="email" required [(ngModel)]="email">
    </div>
    <div class="form-group">
      <label class="label" for="password">Password</label>
      <input type="password" class="form-control" placeholder="Enter password" id="password"  required [(ngModel)]="password">
    </div>
    <button id="submit" type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
