import { Component } from '@angular/core';
import { LoginFormVisibilityService } from 'src/app/shared/login-form-visiblity.service';


@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  loggedIn: boolean = false;
  showLoginForm: boolean = false;
  showLogoutButton: boolean = false;

  constructor(private loginFormVisibilityService: LoginFormVisibilityService) {}
  
  login() {
    // Implement your login logic here
    this.showLoginForm = true;
  }

  logout() {
    // Implement your logout logic here
    this.loggedIn = false;
  }

  onLoginSubmitted() {
    // This function will be called when the login form is submitted
    // Implement your login logic here
    // For simplicity, we will just consider a successful login
    this.loggedIn = true;
    this.showLoginForm = false;
    this.showLogoutButton = true;
  }
}
<nav id="navbar" class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container">
    <a class="navbar-brand" href="#" ><img id="logo" src="assets/LOGO_ICTAK-Name.jpg" alt=""></a>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav ml-auto">
        <li class="nav-item" *ngIf="!loggedIn">
          <button class="btn btn-primary" (click)="login()">Login</button>
        </li>
        <li class="nav-item" *ngIf="loggedIn">
          <button class="btn btn-danger" (click)="logout()">Logout</button>
        </li>
      </ul>
    </div>
  </div>
</nav> 

<app-login *ngIf="showLoginForm" (submitted)="onLoginSubmitted()"></app-login>


<div class="card custom-card" style="width: 18rem;">
  <img src="/assets/pxfuel (1).jpg" class="card-img-top" alt="...">
  <div class="card-body">
    <h5 class="card-title">ICTAK Website</h5>
    <p class="card-text">Click to explore the wide range of courses and certifications you can achieve with us at ICTAK, a joint initiative by the Tech giants and the Government of Kerala</p>
    <a href="https://ictkerala.org/" class="btn btn-info">Click Here</a>
  </div>
</div>

<div id="card1" class="card custom-card" style="width: 18rem;">
  <img src="/assets/banner4.jpg" class="card-img-top" alt="...">
  <div class="card-body">
    <h5 class="card-title">DWMS Kerala</h5>
    <p class="card-text">Register here for more exciting scholarship oppertunities from Kerala Government in the IT field,we provide daily data on vacancies avalable,interships,etc.</p>
    <a href="https://knowledgemission.kerala.gov.in/index.jsp" class="btn btn-info">Click Here</a>
  </div>
</div>
<div class="learner-tracker">
  <h3 class="tracker-title">ICTAK Learner Tracker</h3>
  <p class="tracker-description">ICTAK (Information &amp; Communication Technology Academy of Kerala) learner tracker app is designed to help learners keep track of their progress and achievements in various courses and certifications offered by ICTAK. ICTAK is a joint initiative by the government of Kerala and leading tech giants in the industry.</p>
</div>
