import { Component } from '@angular/core';
import { UserdataService } from 'src/app/userdata.service';

@Component({
  selector: 'app-viewuser',
  templateUrl: './viewuser.component.html',
  styleUrls: ['./viewuser.component.css']
})
export class ViewuserComponent implements OnInit {
  users: any[] = [];

  constructor(private userDataService: UserdataService) { }

  ngOnInit(): void {
    this.userDataService.getUsers().subscribe(
      (users) => {
        this.users = users;
      },
      (error) => {
        console.error(error);
        // Handle error if necessary
      }
    );
  }
}
<!-- navbar -->
<app-navigation></app-navigation>

<!-- add button -->
<div class="addbtn m-3 text-center">
    <button class="btn btn-outline-success" routerLink="/adduser" ><i class="bi bi-person-plus"></i> Add new</button>
</div>

<!-- user details in card form -->
<div class="container text-center d-flex mt-5">
    <div class="row">
        <div class="col-6">
            <div class="card border border-success p-2 mb-2 border-opacity-75" style="width: 18rem;">
                <div class="icon">
                    <img src="assets/person_circle_icon_159926.png" class="card-img-top" alt="...">
                </div>
                <div class="card-body bg-body-tertiary">
                    <h5 class="card-title">Placement Officer</h5>
                    <p class="card-text">Name</p>
                    <p class="card-text">Email</p>
                    <div class="button d-flex justify-content-evenly">
                        <a routerLink="/edituser" class="btn btn-outline-primary"><i class="bi bi-pencil-square"></i> Edit</a>
                        <a class="btn btn-outline-danger"><i class="bi bi-trash3-fill"></i> Delete</a>
                    </div>
                </div>
            </div>
        </div>

        <!-- design view purpose -->
        <div class="col-6">
            <div class="card border border-success p-2 mb-2 border-opacity-75" style="width: 18rem;">
                <div class="icon ">
                    <img src="assets/person_circle_icon_159926.png" class="card-img-top" alt="...">
                </div>
                <div class="card-body bg-body-tertiary">
                    <h5 class="card-title">Training head</h5>
                    <p class="card-text">Name</p>
                    <p class="card-text">Email</p>
                    <div class="button d-flex justify-content-evenly">
                        <a routerLink="/edituser" class="btn btn-outline-primary"><i class="bi bi-pencil-square"></i> Edit</a>
                        <a class="btn btn-outline-danger"><i class="bi bi-trash3-fill"></i> Delete</a>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
